mapper.select:
SELECT *
FROM history h
         join song_account sa
              on h.song_no = sa.song_no
         join krw_account ka
              on h.krw_no = ka.krw_no
where user_no = #{userNo}

    and type_code = #{typeCode}


    and state_code = #{stateCode}


    and between #{beginDate} and #{endDate}
